const initialDate ={
    list:[]
}



const todoReducers = (state=initialDate,action) =>{
    switch(action.type){
        case "ADDTODO":

        const {id,data}=action.payload;
        return{
            ...state,
            list:[
                ...state.list,
                {
                    id:id,
                    data:data

            }
        ]
        }
        default :return state;
    }
}
export default todoReducers
